#include <stdio.h>
#include <stdlib.h>
#define p printf
#define S scanf
void D(void){p("\nInvalid arguments, exiting.");exit(1);}int main(){int a,c,m,hc;int f=6;int b=4;char Ar[128]="arc";char cm[256]="zpaqfranz a";char *d;char *hw;const char *ch;const char *h[] = {"crc32","xxhash","sha1","sha256","xxh3","blake3","sha3","md5","whirlpool","highway64","highway128","highway256","xxhashb","md5b","blake3b","sha256b","sha3b","xxh3b","sha1b"};p("\nssd = 1, hdd = 2: ");S("%d",&a);if(a==1){d="-ssd -ht";}else if(a==2){d="-hdd";}else{D();}p("\narchive name, _ or - instead of spaces: ");S("%s", Ar);p("\nnochecksum = 1, checksum = 2: ");S("%d",&c);if(c==1){ch="nochecksum";hw="-hw";}else if(c==2){p("\nwhich algorithm: ");p("\ncrc32 = 1, xxhash = 2, sha1 = 3, sha256 = 4, xxh3 = 5, blake3 = 6, sha3 = 7, md5 = 8, whirlpool = 9, highway64 = 10, highway128 = 11, highway256 = 12, xxhashb = 13, md5b = 14, blake3b = 15, sha256b = 16, sha3b = 17, xxh3b = 18, sha1b = 19\n");S("%d",&hc);if(hc < 1 || hc > 19){D();}hw="";ch=h[hc - 1];}else{D();}p("\nmethod from 1-5: ");S("%d", &m);if(m < 1 || m > 5){D();}else{p("\nfragments: ");S("%d",&f);p("\nbuffer size in kib: ");S("%d",&b);snprintf(cm,sizeof(cm),"zpaqfranz a %s-m%d_%s-frag%d.zpaq dir -m%d %s -verbose -%s -fragment %d -buffer %d %s",Ar,m,ch,f,m,d,ch,f,b,hw);p("\nGenerated cm:\n%s\n",cm);}return 0;}
